package com.excilys.formation.java.bases03;

public class PairImpair {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i <= 10; i++) {
			if (i%2 == 0){
				System.out.println(i);
			}
		}
		for (int i = 10; i < 20; i++) {
			if (i%2 != 0){
				System.out.println(i);
			}
		}

	}

}
